state.event_update:
unit.SetTargetLocation(unit.GetPosition());

time += DeltaTime;
if (time < 1.0) return CONTINUE;

time = 0;
return REEVALUTATE;

time += DeltaTime;
if (time > 0.5)
{
    if (!leader) return EXIT;

    unit.SetTargetLocation(leader.GetPosition());
    time = 0;

    return REEVALUTATE;
}

return CONTINUE;

transition.guard:
auto group = unit.GetGroup();
if (!group) return FAIL;

auto leader = group.GetLeader();
if (!leader) return FAIL;

vector pos = leader.GetPosition();
if (vector.Distance(pos, unit.GetPosition()) > 50.0) return FAIL;

return SUCCESS;

auto group = unit.GetGroup();
if (!group) return FAIL;

auto leader = group.GetLeader();
if (!leader) return SUCCESS;

vector pos = leader.GetPosition();
if (vector.Distance(pos, unit.GetPosition()) > 50.0) return SUCCESS;

return FAIL;
